<?php/** * Created by PhpStorm. * User: Administrator * Date: 2018/4/20 * Time: 15:14 */class Web_Base extends WeModuleSite{    public function __construct($_init = true)    {        global  $_GPC;        if($_GPC['__input']){            foreach ( $_GPC['__input'] as $k => $v){                $_GPC[$k] = $v;            }        }    }    /**     * 格式化数据返回     */    public function result($errno, $message, $data = '') {        exit(json_encode(array(            'errno' => $errno,            'message' => $message,            'data' => $data,        )));    }}