L DEFAULT '0' COMMENT '0关闭，1开启打印机', `backups_printer` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否打印2份 0 否 1 是', `printer_user` varchar(255) NOT NULL COMMENT '飞蛾打印机账号', `printer_ukey` varchar(255) NOT NULL COMMENT '飞蛾打印机ukey', `printer_sn` varchar(255) NOT NULL COMMENT '飞蛾打印机编码', `aid` int(10) NOT NULL COMMENT '商圈id', `parent_id_1` int(11) NOT NULL COMMENT '一级id', `parent_id_2` int(11) NOT NULL COMMENT '二级id', `parent_id_3` int(11) NOT NULL COMMENT '三级id', `codeimg` varchar(100) NOT NULL COMMENT '小程序码', `codeimgsrc` varchar(200) NOT NULL COMMENT '小程序码路劲', `brand_open` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否关闭商家 0 否 1 是', `time_open` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否营业时间内经营 0 否 1 是', `open_payment` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否启用线下付 0 关闭 1启用', `cimg` varchar(255) DEFAULT NULL COMMENT '商家详情导航图', `is_delivery` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否启用配送 0 关闭 1启用', `delivery_start` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '起配费', `delivery_free` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '免配费', `delivery_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '配送费', `delivery_distance` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '配送范围', `group` text COMMENT '配送设置', `sub_mch_id` varchar(200) NOT NULL COMMENT '商家子账户', `is_counp` tinyint(1) NOT NULL DEFAULT '1' COMMENT '商家单独开启优惠券：1开启，2不开启', PRIMARY KEY (`bid`) ) ENGINE=MyISAM AUTO_INCREMENT=78 DEFAULT CHARSET=utf8;
