CREATE TABLE IF NOT EXISTS `ims_mzhk_sun_withdrawset` ( `id` int(11) NOT NULL AUTO_INCREMENT, `wd_type` varchar(50) NOT NULL DEFAULT '1' COMMENT '（1,2,3）提现方式，1微信支付，2支付宝，3银行打款', `min_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '最低提现金额', `avoidmoney` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '免审金额，可直接提现金额', `wd_content` text NOT NULL COMMENT '提现须知', `cms_rates` float NOT NULL DEFAULT '0' COMMENT '平台佣金比率', `uniacid` int(11) NOT NULL COMMENT 'uniacid', `is_open` tinyint(1) NOT NULL DEFAULT '1' COMMENT '提现开关，2关，1开', `wd_wxrates` float NOT NULL DEFAULT '0' COMMENT '微信提现手续费', `wd_alipayrates` float NOT NULL DEFAULT '0' COMMENT '支付宝提现手续费', `wd_bankrates` float NOT NULL DEFAULT '0' COMMENT '银行卡提现手续费', PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;
