<?phpif (!defined('IN_IA')) {    exit('Access Denied');}if (!function_exists('get_last_day')) {    function get_last_day($year, $month)    {        return date('t', strtotime($year . '-' . $month . ' -1'));    }}if (!function_exists('webUrl')) {    function webUrl($do = '', $query = array(), $full = true)    {        global $_W;        global $_GPC;        $dos = explode('/', trim($do));        $routes = array();        $routes[] = $dos[0];        if (isset($dos[1])) {            $routes[] = $dos[1];        }        if (isset($dos[2])) {            $routes[] = $dos[2];        }        if (isset($dos[3])) {            $routes[] = $dos[3];        }        $r = implode('.', $routes);        if (!empty($r)) {            $query = array_merge(array('r' => $r), $query);        }        $query = array_merge(array('do' => 'web'), $query);        $query = array_merge(array('m' => MODEL_NAME), $query);        if ($full) {            return $_W['siteroot'] . 'web/' . substr(wurl('site/entry', $query), 2);        }        return wurl('site/entry', $query);    }}if (!function_exists('dump')) {    function dump()    {        $args = func_get_args();        foreach ($args as $val) {            echo '<pre style="color: red">';            var_dump($val);            echo '</pre>';        }    }}if (!function_exists('my_scandir')) {    $my_scenfiles = array();    function my_scandir($dir)    {        global $my_scenfiles;        if ($handle = opendir($dir)) {            while (($file = readdir($handle)) !== false) {                if (($file != '..') && ($file != '.')) {                    if (is_dir($dir . '/' . $file)) {                        my_scandir($dir . '/' . $file);                    }                    else {                        $my_scenfiles[] = $dir . '/' . $file;                    }                }            }            closedir($handle);        }    }}// $str = date('Ymd').substr(implode(NULL, array_map('ord', str_split(substr(uniqid(), 7, 13), 1))), 0, 8);// 生成订单编号if (!function_exists('order_sn')) {    function order_sn()    {        $str = date('Ymd').substr(implode(NULL, array_map('ord', str_split(substr(uniqid(), 7, 13), 1))), 0, 8);        return $str;    }}?>